import React from 'react';
import io from 'socket.io-client';

class Chat extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            username: '',
            message: '',
            messages: [],
            users: [],
            auth: false,
            channel: [],
            join: '',
        };
        this.socket = io('localhost:8080');
        this.state.join = localStorage.getItem('join');


        this.sendMessage = ev => {
            let socket_id = JSON.parse(localStorage.getItem('user'))['socket_id'];
            let username = JSON.parse(localStorage.getItem('user'))['username'];
            let channel = localStorage.getItem('join');

            if (this.state.message.includes('/nick')) {
                let dataOldNick = username;
                let dataNick = this.state.message.split(' ');
                let renamed = {
                    socket_id: socket_id,
                    username: dataNick[1]
                };
                localStorage.setItem('user', JSON.stringify(renamed));
                this.setState({message: ''});
                this.socket.emit("RENAME", {
                    socket_id: socket_id,
                    channel: channel,
                    author: "Système",
                    message: dataOldNick + " a changé de pseudo en " + JSON.parse(localStorage.getItem('user'))['username']
                }, dataNick[1], channel);
                this.socket.emit('ADD_USERS', {
                    id: this.socket.id,
                    username: dataNick[1]
                })
            } else if (this.state.message.includes('/create')) {
                let create = this.state.message.split(' ');
                this.socket.emit('CREATE_CHANNEL', create[1])
            } else if (this.state.message.includes('/join')) {
                let join = this.state.message.split(' ');
                this.socket.emit('JOIN_CHANNEL', join[1], username)
            } else if (this.state.message.includes("/delete")) {
                let del = this.state.message.split(' ');
                this.socket.emit('DELETE_CHANNEL', del[1])
            } else if (this.state.message.includes("/leave")) {
                let channels = channel;
                localStorage.removeItem('join');
                this.setState({
                    join: ''
                });
                this.socket.emit("LEAVE_CHANNEL",username,channels);
            } else if (this.state.message.includes('/list')) {
                this.socket.emit('GET_CHANNEL')
            } else if (this.state.message.includes('/users')) {
                this.socket.emit('GET_USERS', channel)
            } else { 
                if (username === "" || username === "Système") {
                    username = "Invité";
                }
                if (localStorage.hasOwnProperty('join')) {
                    this.socket.emit('SEND_CHANNEL_MESSAGE', {
                        socket_id: socket_id,
                        channel: channel,
                        author: username,
                        message: this.state.message,
                    }, channel)
                } else {
                    this.socket.emit('SEND_MESSAGE_GENERAL', {
                        socket_id: socket_id,
                        channel: "General",
                        author: username,
                        message: this.state.message,
                    })
                }
            }
            this.setState({message: ''});
        }

        const listChannel = data =>{
            this.setState({
                channel: data
            });
        }
        const listUsers = data => {
            this.setState({
                users: data
            });
        };
        const addUsers = data => {
            this.setState({
                users: [...this.state.users, data]
            });
        };
        const addMessage = data => {
            console.log(data);
            this.setState({messages: [...this.state.messages, data]});
            console.log(this.state.messages);
        };
        const addChannel = data => {
            this.setState({
                channel: [...this.state.channel, data]
            });
        };
        const changeChannel = channel => {
            this.setState({
                join: channel
            });
        }

        // Message lors d'un nouvel utilisateur
        this.sysMessage = () => {
            this.socket.emit("NEW_USER", {
                author: "Système",
                message: (this.state.username === '' || this.state.username === "Système" ? "Invité" : this.state.username) + " à rejoint le salon.",
                pseudo: (this.state.username === '' || this.state.username === "Système" ? "Invité" : this.state.username),
            });
            this.setState({message: ''});
            var info = {
                socket_id: this.socket.id,
                username: (this.state.username === '' || this.state.username === "Système" ? "Invité" : this.state.username)
            };
            var user_profile = []
            user_profile.push(info)
            localStorage.setItem('user', JSON.stringify(info));
        };

        this.socket.on("NEW_USER_MESSAGE", function(data) {
            addMessage(data);
        });

        this.socket.on('ADD_CHANNEL', function(channel) {
            addChannel(channel)
        });

        this.socket.on('CHANGE_CHANNEL', function(channel) {
            localStorage.setItem('join', channel);
            changeChannel(channel)
        });

        this.socket.on("RECIEVE_MESSAGE", function(data) {
            addMessage(data);
        });

        this.socket.on('LIST_CHANNEL', function(channel) {
            listChannel(channel);
        });

        this.socket.on('LIST_USERS', function(users) {
            listUsers(users);
        });

        this.socket.on('ADD_USERS', function(users) {
            addUsers(users)
        });

        // Gestion des salons

        // Gérer l'input de l'username pour rejoindre la chatroom
        this.handleLogin = this.handleLogin.bind(this);
    }

    handleLogin() {
        this.setState({auth: true});
        this.setState({join: 'General'});
        this.sysMessage();
        localStorage.setItem('join', 'General');
    }
    componentWillMount() {
        localStorage.removeItem('join');
        localStorage.removeItem('users');
    }

    handleKeyPress = (event) => {
        if(event.key === 'Enter'){
            this.sendMessage;
        }
    }

    render() {
        if (this.state.auth === false) {
            return (
                <div className="container">
                    <div className="row">
                        <div className="col-4">
                            <div className="card">
                                <div className="card-body">
                                    <div className="card-title">Ton pseudo :</div>
                                    <hr />
                                    <div className="footer">
                                        <input type="text" placeholder="Pseudo" value={this.state.username} onChange={ev => this.setState({username: ev.target.value})} className="form-control"/>
                                        <br />
                                        <button className="btn btn-primary form-control" onClick={this.handleLogin}>Send</button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            )
        } else {
            if (this.state.username === "" || this.state.username === "Système") {
                this.setState({username: "Invité"});
            }
            if (this.state.join === "") {
                this.setState({join: "General"});
            }
            return (
                <div className="container">
                    <div className="row">
                        <div className="col-4">
                            <div className="card">
                                <div className="card-body">
                                    <div className="card-title">Salons</div>
                                    <hr />
                                    <div className="salons">
                                    {this.state.channel.map(chat => {
                                        return (
                                        <a class="btn btn-outline-danger" href>{chat.name}</a>)
                                        })}
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="col-6">
                            <div className="card">
                                <div className="card-body">
                                    <div className="card-title font-weight-bold">#{this.state.join}</div>
                                    <hr/>
                                    <div className="messages">
                                        {this.state.messages.map(message => {
                                            if (message.author === "Système") {
                                                return (
                                                    <div className="text-danger">{message.author}: {message.message}</div>
                                                )
                                            } else {
                                                return (
                                                    <div>{message.author}: {message.message}</div>
                                                )
                                            }
                                        })}
                                    </div>
                                    <div className="footer">
                                        <input type="text" placeholder="Message" className="form-control" value={this.state.message} onChange={ev => this.setState({message: ev.target.value})}/>
                                        <br/>
                                        <button className="btn btn-primary form-control" onKeyPress={this.handleKeyPress} onClick={this.sendMessage}>Envoi</button>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="col-2">
                            <div className="card">
                                <div className="card-body">
                                    <div className="card-title">Utilisateurs</div>
                                    <hr />
                                    <div className="users">
                                    {this.state.users.map(user => {
                                        return (
                                            <div class="user-item">{user.username}</div>
                                        )
                                    })}
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            );
        }
    }
}

export default Chat;
